              <INPUT name="phone"  onChange={event => telefono(event.target.value)} type="text" placeholder='Phone' />
              <INPUT name="contraseña"  onChange={event => contraseña(event.target.value)} type="text"  placeholder='Password'/>
          </FORM>

      </Div7>
     
    
      <BtnNe type="submit">Create</BtnNe>
      
      
     </Section2>
    )
  }

}

export default Register;
