import React, { Component } from 'react'


import { Div6, Div7, INPUT, H1, P, BtnNe, FORM, Section2 } from "../styles/styles"



class Register extends Component {

  constructor() {
    super()
    this.state = {


      textnombre: '',
      textmail: '',
      texttelefono: '',
      textcontraseña: '',

    }



    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);


  }

  componentDiMount() {
    setTimeout(() => {
      this.setState({
        loader: false

      })

    });

    this.createData()
  }

  createData = () => {

  }

  handleChange(e) {
    const nombre = this.setState({ textnombre: e.target.value });
    this.setState({ textmail: e.target.value });
    this.setState({ texttelefono: e.target.value });
    this.setState({ textcontraseña: e.target.value });
    localStorage.setItem('user', JSON.stringify(this.state.textnombre))
  }


  handleSubmit = (e) => {
    e.preventDefault()

  }

  render() {

    return (
      <Section2>
        <Div6 >
          <H1>Create new Account</H1>

          <P>Create a new account by filling in all the
            fields or log in to an existing account</P>



        </Div6>

        <Div7>
          <FORM onSubmit={this.handleSubmit}>
            <div onChange={this.handleChange}>
              <INPUT name='nombre' value={this.state.value} placeholder='Name and lastname' />
              <INPUT name='mail' value={this.state.value} placeholder='mail' />
              <INPUT name='telefono' value={this.state.value} placeholder='Telefono' />
              <INPUT name='contraseña' value={this.state.value} placeholder='Contraseña' />
            </div>
          </FORM>

        </Div7>


        <BtnNe type="submit">Create</BtnNe>


      </Section2>
    )
  }

}

export default Register;
