     <Div7>
          <FORM onSubmit={this.handleSubmit}>
              <INPUT name='nombre' onChange={this.handleChange} value={this.state.text} placeholder='Name and lastname' />
   
          </FORM>

      </Div7>
     
    
      <BtnNe type="submit">Create</BtnNe>
      
      
     </Section2>
    )
  }

} 

export default Register;
