import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { Div7, Div6,H1 ,INPUT,P,FORM,BtnNe, Footer} from '../styles/styles';
import axios from 'axios'

const url = "http://localhost:3005/data"

class InterfaceLogin extends Component {

    constructor(props) {
        super(props)

        this.state = {
            data: [],
            textnombre: '',
            textmail: '',
            texttelefono: '',
            textcontraseña: '',

            loader: false,
        }
    }

    /////////Montaje de estado
    componentDidMount() {

        setTimeout(() => {
            this.setState({
                loader: true
            })
        })
    }
    ////////////////consumo api
    componentDidMount() {
        this.peticionGet()
    }
    
///////////////////////////////Login y validacion por submit////////////////////////

handleSubmit = (e) => {
    e.preventDefault();
    
  
    telefono: this.state.texttelefono;
    contraseña: this.state.textcontraseña;
    
    peticionGet = async () => {
        let { data } = await axios.get(url)
        console.log(data)
    }
  

  }


  ////////////////////////////////////////////////////////////////////////////////////////////////////////
    render() {
        return (
           <section>
            <Div6 >
                <H1>Welcome back</H1>
         
                <P>Sign in to an existing account
                    using your phone number</P>
           
            
            
            </Div6>

           <Div7>
                <FORM>
                    <INPUT value={this.state.texttelefono} name="texttelefono" onChange={this.handleChangeTelefono} type="number" placeholder='Number Phone' />
                    <INPUT value={this.state.textcontraseña} name="textcontraseña" onChange={this.handleChangeContraseña} type="password"  placeholder='Password'/>
               
                    <BtnNe  type="submit">Login</BtnNe>
                </FORM>

            </Div7>
            
            <Footer>


            <h6>Don’t have account ? </h6><Link to="/CreateAcount"> <h6>Sign up</h6></Link>
           </Footer> 
           </section>
        )
    }
}

export default InterfaceLogin;
