import React, { Component } from 'react'
import { ListUser } from '../data/Users';


import { Div6, Div7, INPUT, H1, P, BtnNe, FORM, Section2 } from "../styles/styles"



class Register extends Component {

  constructor() {
    super()
    this.state = {
loader:false,
user:{

      textnombre: '',
      textmail: '',
      texttelefono: '',
      textcontraseña: '',

    }} }

    componentDidMount(){
      setTimeout(()=> {
        this.setState({
          loader:true
        })
      })
    this.createUser()
 }


createUser = () => {
this.setState({
  user:{
    textnombre: handleChangeName,
    textmail: handleChangeEmail,
    texttelefono: handleChangeTelefono,
    textcontraseña: handleChangeContraseña,
}
})}


   handleChangeName=(e)=>{
    this.setState({ textnombre: e.target.value })};
  
  handleChangeEmail=(e)=>{
    this.setState({ textmail: e.target.value })};
  
  handleChangeTelefono=(e)=>{
    this.setState({ texttelefono: e.target.value });
     console.log(this.state.texttelefono)}
  
  handleChangeContraseña=(e)=>{
    this.setState({ textcontraseña: e.target.value });
     console.log(this.state.textcontraseña)  }



  handleSubmit = (e) => {
    e.preventDefault()
   console.log(this.state.user)
  }





  render() {
    return (
      <Section2>
        <Div6 >
          <H1>Create new Account</H1>

          <P>Create a new account by filling in all the
            fields or log in to an existing account</P>



        </Div6>

        <Div7>
          <form onSubmit={this.handleSubmit}>
            <div>
              <INPUT value='name' onChange={this.handleChangeName} type="text"   placeholder='Name and lastname' />
              <INPUT value='Email' onChange={this.handleChangeEmail} type="email"   placeholder='mail' />
              <INPUT value='phone' onChange={this.handleChangeTelefono} type="number"   placeholder='Telefono' />
              <INPUT value='password' onChange={this.handleChangeContraseña} type="password"  placeholder='Contraseña' />
            </div>
                 <BtnNe  type="submit">Create</BtnNe>
          </form>

        </Div7>


   


      </Section2>
    )
  }

}

export default Register;
