import React, { Component } from 'react'


import { Div6, Div7, INPUT , H1, P, BtnNe, FORM,Section2} from "../styles/styles"



class Register extends Component {

  constructor(){
    super()
    this.state = {
      loader:true,
      textnombre:'',
      textmail:'',
      texttelefono:'',
      textcontraseña:'',
    },
  
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

  
    }

handleChange(e){
  this.setState({textnombre: e.target.value});
 this.setState({textmail: e.target.value});
  this.setState({texttelefono: e.target.value});
  this.setState({textcontraseña: e.target.value});
 
  }


 handleSubmit=(e)=>{
  e.preventDefault()

const newItem = {
  text: this.state.text,
  id: Date.now()
}
this.setState(state => ({
  items: state.items.concat(newItem),
  text: ''

  
 }))}



  render() {

    return (
      <Section2>
      <Div6 >
          <H1>Create new Account</H1>
   
          <P>Create a new account by filling in all the
fields or log in to an existing account</P>
     
      
      
      </Div6>

     <Div7>
          <FORM onSubmit={this.handleSubmit}>
              <INPUT name='nombre'  onChange={this.handleChange} value={this.state.value} placeholder='Name and lastname' />
              <INPUT  name='mail' onChange={this.handleChange} value={this.state.value} placeholder='mail' />
              <INPUT  name='telefono' onChange={this.handleChange} value={this.state.value} placeholder='Telefono' />
              <INPUT  name='contraseña' onChange={this.handleChange} value={this.state.value} placeholder='Contraseña' />
   
          </FORM>

      </Div7>
     
    
      <BtnNe type="submit">Create</BtnNe>
      
      
     </Section2>
    )
  }

} 

export default Register;
