import React, { Component } from 'react'
import { ListUser } from '../data/Users';


import { Div6, Div7, INPUT, H1, P, BtnNe, FORM, Section2 } from "../styles/styles"



class Register extends Component {

  constructor() {
    super()
    this.state = {
loader:true,
user:{

      textnombre: '',
      textmail: '',
      texttelefono: '',
      textcontraseña: '',

    }} }

    componentDidMount(){
      setTimeout(()=> {
        this.setState({
          loader:false
        })
      })
    
  this.handleChangeName = this.handleChangeName.bind(this);
    this.handleChangeEmail = this.handleChangeEmail.bind(this);
    this.handleChangeTelefono = this.handleChangeTelefono.bind(this);
    this.handleChangeContraseña = this.handleChangeContraseña.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.createUser()
 }

createUser = () => {

this.setState({
  user:{

    textnombre: textnombre,
    textmail: textmail,
    texttelefono: texttelefono,
    textcontraseña: textcontraseña,
}
})

}


  handleChangeName(e) {
    this.setState({ textnombre: e.target.value });
   
    
  }
  handleChangeEmail(e){
    this.setState({ textmail: e.target.value });
    
  }
  handleChangeTelefono(e){
    this.setState({ texttelefono: e.target.value });
     console.log(this.state.texttelefono)
  }
  handleChangeContraseña(e){
    this.setState({ textcontraseña: e.target.value });
     console.log(this.state.textcontraseña)
  }


  handleSubmit = (e) => {
    e.preventDefault()
   
  }

  render() {

   console.log(this.state.user)

    return (
      <Section2>
        <Div6 >
          <H1>Create new Account</H1>

          <P>Create a new account by filling in all the
            fields or log in to an existing account</P>



        </Div6>

        <Div7>
          <form onSubmit={this.handleSubmit}>
            <div>
              <INPUT onChange={this.handleChangeName} type="text"   placeholder='Name and lastname' />
              <INPUT onChange={this.handleChangeEmail} type="email"   placeholder='mail' />
              <INPUT onChange={this.handleChangeTelefono} type="number"   placeholder='Telefono' />
              <INPUT  onChange={this.handleChangeContraseña} type="password"  placeholder='Contraseña' />
            </div>
                 <BtnNe   type="submit">Create</BtnNe>
          </form>

        </Div7>


   


      </Section2>
    )
  }

}

export default Register;
